# when the user responds to an item, it returns the Value of their response. We need to take this value and then find out what response column it refers to in the item bank (i.e. response 0? response 4?)
getResponseNumber = function(response = NULL, questions) {
  print("matching response number")
  print(response)
  options = NULL
  o = question$response_options
  if(is.character(o)) { o = fromJSON(o) }
  if(length(o) > 0) {
    #response options
    for(i in 1:length(o)) {
      options = rbind(options, as.list(o[[i]]))
    }

    for(i in 1:dim(options)[1]) {
      option = options[i,]
      if (is.null(response)) { #user did not respond to the question. Presumably the time ran out or something like that. In that case, just take the middle option.
        print(paste0("Warning: User did not respond to the question. Taking option ", round((dim(options)[1] + 0.1) / 2, 0), " as their response.")) #here the first response is number 1. below it's 0 which is why we do -1.
        return (round((dim(options)[1] + 0.1) / 2, 0) - 1) #do rounding because there might be an odd number of responses. R rounding takes 0.5 down to 0, so add 0.1 so that it's 0.6 and goes up to 1.
      } else if (option$value == response) { #have we found the correct response option?
        return (i - 1) #i - 1 because catR wants the responses to start from 0. 
      }
    }

    stop("User's response did not match any of the options in the database")

  } else {
    stop("Question does not have any response options")
  }

}

