      maxresponses = length(p)
    } 
  }

  param_bank = matrix(NA, ncol=maxresponses, nrow=nrow(questions)) #set up empty matrix with max cols defined by max response options, and nrow defined by number of questions

  for (i in 1:nrow(questions)) {   
    p = questions[i,]$response_options

    if(is.character(p)) { 
      p = fromJSON(p)
    }

    for(j in 1:length(p)) {
      param_bank[i,j] = p[[j]]$irt_parameter #puts each parameter into the matrix one at a time.
    } 

  }   

  param_bank = apply(param_bank, 2, function (x) { as.numeric(x) } ) #make it numeric

  param_bank = param_bank[,-1] #get rid of first column which should just be NAs.

  return(param_bank)

}

#look for any obvious mistakes in the parameter bank and give out warnings.
checkParamBank = function (poly_model, param_bank, questions) {
  print("Parameter bank:")
  print(param_bank)

  if (poly_model == "GRM" | poly_model == "GPCM") {
    # look for any missing discriminations
    missed = which(is.na(param_bank[,1]))
    missing_ids = questions[missed,which(colnames(questions) == "id")]
    if (length(missing_ids) > 0) {
      print(paste0(c("Warning: The following item ids do not have a discrimination parameter set, as required by your model, and therefore will only be used if there are no other valid items:", missing_ids, ". See the parameter bank above; the first column contains the discrimination parameter."), collapse=" "))
    }
  }

  if (poly_model == "GRM" | poly_model == "GPCM") {
    diffcol = 2
  } else {
    diffcol = 1
  }

  #look for missing difficulty parameters from first column (i.e. it will only find a problem if the first column is missing)
  missed = which(is.na(param_bank[,diffcol]))
  missing_ids = questions[missed,which(colnames(questions) == "id")]
  if (length(missing_ids) > 0) {
    stop(paste0(c("The following item ids are missing a difficulty parameter for the first response option:", missing_ids, ". See the parameter bank above."), collapse=" ")) 
  }
  return(TRUE)
}

#gets the parameter bank. Based on the type of model there are different parameters.
getParamBank = function(poly_model, questions) {
  print("getParamBank")
  #print(questions)
  #print(questions[1,]$response_options)
  #print(poly_model)



  if (poly_model == "PCM") { #PCM has just the parameters based on number of response options - 1.

    param_bank = unlistParameters(questions)

  } else if (poly_model == "GRM") { #GRM has the same parameters as response options, but one of them is discrimination.
    param_bank = unlistParameters(questions)

    #add the column of discriminations    
    discrims = questions[,which(colnames(questions)=="irt_discrimination")]
    param_bank = cbind(as.numeric(discrims), param_bank)

  } else if (poly_model == "GPCM") { #GPCM has the same parameters as response options, but one of them is discrimination.

    #add the column of discriminations    
    discrims = questions[,which(colnames(questions)=="irt_discrimination")]
    param_bank = cbind(as.numeric(discrims), param_bank)

  }
  checkParamBank(poly_model, param_bank, questions)

  return(param_bank)
}

